import { useNavigate } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const LoginForm = () => {
    const [email, setemail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/api/v1/user/logincredentials', {
        email,
        password,
      }, {
        headers: {
          'Content-Type': 'application/json',
        }
      });

      const data = response.data;
      if(data.email=== email && data.password === password){
        alert("Successful" + email);
      localStorage.setItem('storedRole', data.role);
      localStorage.setItem('email', data.email);

      if(data.role ==="admin"){
        navigate('/')
      }
    }

      
      navigate('/nav'); // Navigate to dashboard on successful login
    } catch (error) {
      setError('Invalid email or password'); // Set error message
      console.error('login error', error);
    }
  };

  useEffect(() => {
    localStorage.removeItem('email');
    localStorage.removeItem('storedRole');
  }, []);

  const handleSignup = () => {
    navigate('/signup');
  };

  const handleForgotPassword = () => {
    navigate('/forgot-password');
  };

      
    

    return (
        <div className="login-form">
            <h2 style={{ color: '#1b5374' }}>ONLINE REPORTING EVENTS SYSTEM</h2>
             <form onSubmit={handleSubmit}>
                <input
                    type="email"
                    placeholder="please enter your email"
                                    
                    value={email}
                    onChange={(e) => setemail(e.target.value)}
                    
                /><br /><br />
                <input
                     type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
                /><br />
                <button type="submit">Login</button>
             </form> 
             {error && <div className="error-message">{error}</div>}
             

        </div>
    ); 
};

export default LoginForm;
