import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Nav from '../Navigation/Nav';

const EventView = () => {
  const personId = parseInt(localStorage.getItem('userId'));

  const [events, setEvent] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const [currentEvent, setCurrentEvent] = useState(null);
  const [formData, setFormData] = useState({
    event_name: '',
    event_type: '',
    event_location: '',
    time_posted: '',
    image: ''
  });

  useEffect(() => {
    axios.get("http://localhost:8080/api/v1/event/all")
      .then((response) => {
        const filterEventByUserId = response.data.filter(item => item.person.userId === personId);
        setEvent(filterEventByUserId);
      })
      .catch((error) => {
        console.error("There was an error fetching events!", error);
      });
  }, [personId]);

  // const handleUpdateClick = (event) => {
  //   console.log("Update button clicked");
  //   setCurrentEvent(event);
  //   setFormData({
  //     event_name: event.event_name,
  //     event_type: event.event_type,
  //     event_location: event.event_location,
  //     time_posted: event.time_posted,
  //     image: event.image
  //   });
  //   console.log("Show Popup State Before:", showPopup);
  //   setShowPopup(true);
  //   console.log("Show Popup State After:", showPopup);
  // };
  const handleUpdateClick = () => {
    console.log("Update button clicked");
  };
  
  const handleUpdate = (e) => {
    e.preventDefault();
    axios.put(`http://localhost:8080/api/v1/event/update/${currentEvent.event_id}`, formData)
      .then((response) => {
        setEvent(events.map(event => 
          event.event_id === currentEvent.event_id ? response.data : event
        ));
        setShowPopup(false);
      })
      .catch((error) => {
        console.error("There was an error updating the event!", error);
      });
  };

  const handleDelete = (eventId) => {
    if (window.confirm('Are you sure you want to delete this event?')) {
      axios.delete(`http://localhost:8080/api/v1/event/delete/${eventId}`)
        .then((response) => {
          setEvent(events.filter(event => event.event_id !== eventId));
        })
        .catch((error) => {
          console.error("There was an error deleting the event!", error);
        });
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  return (
    <>
      <Nav />
      <div className="table-container">
        <h4>View Event {personId}</h4>
        <table className="event-table">
          <thead>
            <tr>
              <th>Event ID</th>
              <th>Event Name</th>
              <th>Event Type</th>
              <th>Event Location</th>
              <th>Time Posted</th>
              <th>Image</th>
              <th>Action</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {events.map((event) => (
              <tr key={event.event_id}>
                <td>{event.event_id}</td>
                <td>{event.event_name}</td>
                <td>{event.event_type}</td>
                <td>{event.event_location}</td>
                <td>{event.time_posted}</td>
                <td>
                  <img
                    src={`data:image/png;base64,${event.image}`}
                    alt={event.event_name}
                    className="event-image"
                  />
                </td>
                <td>
                <button className='btn' onClick={() => handleUpdateClick(event)}>Update</button>

                </td>
                <td>
                  <button className='btn' onClick={() => handleDelete(event.event_id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {showPopup && (
          <div className="popup">
            <div className="popup-content">
              <span className="close" onClick={() => setShowPopup(false)}>&times;</span>
              <h2>Update Event</h2>
              <form onSubmit={handleUpdate}>
                <label>
                  Event Name:
                  <input
                    type="text"
                    name="event_name"
                    value={formData.event_name}
                    onChange={handleChange}
                    required
                  />
                </label>
                <label>
                  Event Type:
                  <input
                    type="text"
                    name="event_type"
                    value={formData.event_type}
                    onChange={handleChange}
                    required
                  />
                </label>
                <label>
                  Event Location:
                  <input
                    type="text"
                    name="event_location"
                    value={formData.event_location}
                    onChange={handleChange}
                    required
                  />
                </label>
                <label>
                  Time Posted:
                  <input
                    type="text"
                    name="time_posted"
                    value={formData.time_posted}
                    onChange={handleChange}
                    required
                  />
                </label>
                <label>
                  Image (Base64):
                  <textarea
                    name="image"
                    value={formData.image}
                    onChange={handleChange}
                    required
                  />
                </label>
                <button type="submit">Update</button>
              </form>
            </div>
          </div>
        )}
      </div>
    </>
  );
};

export default EventView;
