import React, { useState, useEffect } from 'react';
import Nav from '../Navigation/Nav';

const EventStatistics = () => {
    const [eventStats, setEventStats] = useState({
        crimeEvents: 0,
        fireEvents: 0,
        smugglingEvents: 0,
    });

    useEffect(() => {
        // Fetch event statistics from the backend API
        // Example: Assuming the API returns a JSON object with the stats
        fetch('/api/event-stats')
            .then(response => response.json())
            .then(data => {
                setEventStats({
                    crimeEvents: data.crimeEvents,
                    fireEvents: data.fireEvents,
                    smugglingEvents: data.smugglingEvents,
                });
            });
    }, []);

    return (
          <> <Nav/>
        <div className="event-statistics">
            <h2>Event Statistics</h2>
            <div className="graph-container">
                <div className="graph">
                    <h3>Crime Events</h3>
                    <div className="bar" style={{ height: `${eventStats.crimeEvents * 10}px` }}>
                        <span>{eventStats.crimeEvents}</span>
                    </div>
                </div>
                <div className="graph">
                    <h3>Fire Events</h3>
                    <div className="bar" style={{ height: `${eventStats.fireEvents * 10}px` }}>
                        <span>{eventStats.fireEvents}</span>
                    </div>
                </div>
                <div className="graph">
                    <h3>Smuggling Events</h3>
                    <div className="bar" style={{ height: `${eventStats.smugglingEvents * 10}px` }}>
                        <span>{eventStats.smugglingEvents}</span>
                    </div>
                </div>
            </div>
        </div>
        </>
    );
};

export default EventStatistics;
