import React, { useState } from 'react';
import './EventUpload.css';

const EventUpload = () => {
    const [eventDetails, setEventDetails] = useState({
        eventName: '',
        eventType: '',
        eventLocation: '',
        eventDate: '',
        eventTime: '',
        eventImage: null,
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEventDetails(prevDetails => ({
            ...prevDetails,
            [name]: value,
        }));
    };

    const handleImageChange = (e) => {
        setEventDetails(prevDetails => ({
            ...prevDetails,
            eventImage: e.target.files[0],
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Handle form submission and image upload logic here
        console.log('Event Details:', eventDetails);
        alert('Event has been submitted.');
    };

    return (
        
        <div className="event-upload">
            <h1>Submit Event</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Event Name:
                    <input
                        type="text"
                        name="eventName"
                        value={eventDetails.eventName}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Type of Event:
                    <select
                        name="eventType"
                        value={eventDetails.eventType}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Select Event Type</option>
                        <option value="tukio la moto">Tukio la Moto</option>
                        <option value="tukio la magendo">Tukio la Magendo</option>
                        <option value="tukio la uhalifu">Tukio la Uhalifu</option>
                    </select>
                </label>
                <label>
                    Event Location:
                    <input
                        type="text"
                        name="eventLocation"
                        value={eventDetails.eventLocation}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Event Date:
                    <input
                        type="date"
                        name="eventDate"
                        value={eventDetails.eventDate}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Event Time:
                    <input
                        type="time"
                        name="eventTime"
                        value={eventDetails.eventTime}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Event Image:
                    <input
                        type="file"
                        accept="image/*"
                        onChange={handleImageChange}
                        required
                    />
                </label>
                <button type="submit">Submit Event</button>
            </form>
        </div>
    );
};

export default EventUpload;
