import React, { useState, useEffect } from 'react';
import Nav from '../Navigation/Nav';


const EventStatistics = () => {
    const [eventStats, setEventStats] = useState({
        crimeEvents: 0,
        fireEvents: 0,
        smugglingEvents: 0,
    });

    useEffect(() => {
        // Fetch event statistics from the backend API
        fetch(`http://localhost:8080/api/v1/event/add/${p}`)
            .then(response => response.json())
            .then(data => {
                setEventStats({
                    crimeEvents: data.crimeEvents,
                    fireEvents: data.fireEvents,
                    smugglingEvents: data.smugglingEvents,
                });
            });
    }, []);

    return (
        <> 
        <Nav />
        <div className="event-statistics">
            <h2>Event Statistics</h2>
            <div className="card-container">
                <div className="card">
                    <h3>Crime Events</h3>
                    <p>{eventStats.crimeEvents}</p>
                </div>
                <div className="card">
                    <h3>Fire Events</h3>
                    <p>{eventStats.fireEvents}</p>
                </div>
                <div className="card">
                    <h3>Smuggling Events</h3>
                    <p>{eventStats.smugglingEvents}</p>
                </div>
            </div>
        </div>
        </>
    );
};

export default EventStatistics;
