import React, { useState, useEffect } from 'react';
import Nav from '../Navigation/Nav';
import axios from 'axios';


const EventStatistics = () => {
    const [crimeevent, setCrimeEvent] = useState(0);
     useEffect(()=>{
        axios.get('http://localhost:8080/api/v1/event/count')

    .then((response)=>{
        setCrimeEvent(response.data);
        const filterCrimeEvent = response.data
    })
     })
     const [FireEvent, setFireEvent] = useState(0);
     useEffect(()=>{
        axios.get('http://localhost:8080/api/v1/event/count')
        .then((response)=>{
            setFireEvent(response.data);
        })
     })

     const[SmugglingEvent, setSmugglingEvent] = useState(0);
     useEffect(()=>{
        axios.get('http://localhost:8080/api/v1/event/count')
        .then((response)=>{
            setSmugglingEvent(response.data)
        })
     })




    return (
        <> 
        <Nav />
        <div className="event-statistics">
            <h2>Event Statistics</h2>
            <div className="card-container">
                <div className="card">
                    <h3>{crimeevent}</h3>
                    <p>Crime Event</p>
                </div>
                <div className="card">
                    <h3>{FireEvent}</h3>
                    <p>Fire Event</p>
                </div>
                <div className="card">
                    <h3>{SmugglingEvent}</h3>
                    <p>Smuggling Event</p>
                </div>
            </div>
        </div>
        </>
    );
};

export default EventStatistics;
