import React, { useState } from 'react';
import Nav from '../Navigation/Nav';

const EventPost = () => {
    const [eventDetails, setEventDetails] = useState({
        eventName: '',
        eventLocation: '',
        eventDate: '',
        eventTime: '',
        eventDescription: '',
        eventImage: null,
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEventDetails(prevDetails => ({
            ...prevDetails,
            [name]: value,
        }));
    };

    const handleImageChange = (e) => {
        setEventDetails(prevDetails => ({
            ...prevDetails,
            eventImage: e.target.files[0],
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('eventName', eventDetails.eventName);
        formData.append('eventLocation', eventDetails.eventLocation);
        formData.append('eventDate', eventDetails.eventDate);
        formData.append('eventTime', eventDetails.eventTime);
        formData.append('eventDescription', eventDetails.eventDescription);
        formData.append('eventImage', eventDetails.eventImage);

        // Handle form submission, e.g., sending formData to the server
        console.log('Event Details Submitted:', eventDetails);
    };

    return (
        <><Nav />
        <div className="event-post">
            <h1>Post an Event</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Event Name:
                    <input
                        type="text"
                        name="eventName"
                        value={eventDetails.eventName}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Event Location:
                    <input
                        type="text"
                        name="eventLocation"
                        value={eventDetails.eventLocation}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Event Date:
                    <input
                        type="date"
                        name="eventDate"
                        value={eventDetails.eventDate}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Event Time:
                    <input
                        type="time"
                        name="eventTime"
                        value={eventDetails.eventTime}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Event Description:
                    <textarea
                        name="eventDescription"
                        value={eventDetails.eventDescription}
                        onChange={handleChange}
                    />
                </label>
                <label>
                    Event Image:
                    <input
                        type="file"
                        accept="image/*"
                        onChange={handleImageChange}
                        required
                    />
                </label>
                <button type="submit">Submit Event</button>
            </form>
        </div>
        </>
    );
};

export default EventPost;
