import React, { useState, useEffect } from 'react';
import Nav from '../Navigation/Nav';
// import './ViewResponse.css'; // add a separate CSS file for styling

const ViewResponse = () => {
  const [responseData, setResponseData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/v1/response/all');
        const data = await response.json();
        setResponseData(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <> <Nav />
    <div className="table-container" style={{ marginTop: "150px" }}>
        <h2>View Event {personId}</h2>
        <table className="event-table">
          <thead>
            <tr>
              <th>Event ID</th>
              <th>Event Name</th>
              <th>Event Type</th>
              <th>Event Location</th>
              <th>Shehia Name</th>
              <th>Time Posted</th>
              <th>Image</th>
              <th>Action</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {events.map((event) => (
              <tr key={event.event_id}>
                <td>{event.event_id}</td>
                <td>{event.event_name}</td>
                <td>{event.event_type}</td>
                <td>{event.event_location}</td>
                <td>{event.shehia.shehiaName}</td>
                <td>{event.time_posted}</td>
                <td>
                  <img
                    src={`data:image/png;base64,${event.image}`}
                    alt={event.event_name}
                    className="event-image"
                  />
                </td>
                
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default ViewResponse;
