import React, { useState } from 'react';
import axios from 'axios'; // Ensure axios is installed

const Update= ({ eventId, event, handleUpdate }) => {
    const [eventName, setEventName] = useState(event.event_name);
    const [eventType, setEventType] = useState(event.event_type);
    const [eventLocation, setEventLocation] = useState(event.event_location);
    const [timePosted, setTimePosted] = useState(event.time_posted);
    const [image, setImage] = useState(null); // Changed to null initially

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        const updatedEvent = {
            event_id: eventId,
            event_name: eventName,
            event_type: eventType,
            event_location: eventLocation,
            time_posted: timePosted,
        };

        // Create a FormData object to handle file uploads
        const formData = new FormData();
        for (const key in updatedEvent) {
            formData.append(key, updatedEvent[key]);
        }
        if (image) {
            formData.append('image', image);
        }

        try {
            // Call the handleUpdate function. This function should send the formData to your backend.
            await handleUpdate(eventId, formData);
            alert('Event updated successfully!');
        } catch (error) {
            console.error('Error updating event:', error);
            alert('Failed to update event. Please try again.');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Event Name:
                <input 
                    type="text" 
                    value={eventName} 
                    onChange={(e) => setEventName(e.target.value)} 
                    required 
                />
            </label>
            <br />
            <label>
                Event Type:
                <input 
                    type="text" 
                    value={eventType} 
                    onChange={(e) => setEventType(e.target.value)} 
                    required 
                />
            </label>
            <br />
            <label>
                Event Location:
                <input 
                    type="text" 
                    value={eventLocation} 
                    onChange={(e) => setEventLocation(e.target.value)} 
                    required 
                />
            </label>
            <br />
            <label>
                Time Posted:
                <input 
                    type="datetime-local" 
                    value={timePosted} 
                    onChange={(e) => setTimePosted(e.target.value)} 
                    required 
                />
            </label>
            <br />
            <label>
                Image:
                <input 
                    type="file" 
                    onChange={(e) => setImage(e.target.files[0])} 
                />
            </label>
            <br />
            <button type="submit">Update Event</button>
        </form>
    );
};

export default Update;
