import { Link, useLocation, useNavigate } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const LoginForm = () => {
    const [email, setemail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const Location = useLocation();
  useEffect(()=>{
    if (location.pathname ==='/') {
      localStorage.removeItem('userId');
      localStorage.removeItem('role')
      
    }
  })

  
 const handleSubmit =async (event) =>{
  event.preventDefault();
  try{
    const response = await axios.get(`http://localhost:8080/api/v1/user/login/${email}`)
    const userData = await response.data
    console.log(userData.email);

    if(userData.email === email  && userData.password === password){

      localStorage.setItem("role", userData.role)

      localStorage.setItem("userId", userData.userId)

      if(userData.role === "Admin"){
        alert("Hi admin")
        navigate('/statistic')
      }

      if(userData.role === "Person"){
        navigate('/statistic');
        alert("hello person")
      }

      if (userData.role === "Sheha"){
        localStorage.setItem("shehiaId",userData.shehia.shehiaId);
        alert("hello sheha")
        navigate('/statistic')
      }

      if(userData.role === "officer"){
        alert("Hi officer" )
        navigate('/statistic')
      }
      alert("success")
    }else{
      alert("fail")
    }
    
  }catch(error){
    console.error(error)
  }
 }

  const handleSignup = () => {
    navigate('/signup');
  };

  const handleForgotPassword = () => {
    navigate('/forgot-password');
  };

      
    

    return (
        <div className="login-form">
            <h2 style={{ color: '#1b5374' }}>ONLINE REPORTING EVENTS SYSTEM</h2>

             <form onSubmit={handleSubmit}>
                <input
                    type="email"
                    placeholder="please enter your email"
                                    
                    value={email}
                    onChange={(e) => setemail(e.target.value)}
                    
                /><br /><br />
                <input
                     type="password"
                     placeholder='enter your password'
            value={password}
            onChange={(e) => setPassword(e.target.value)}
                /><br />
                <button type="submit">Login</button>
                <br></br>
                <br></br>
                <Link to={'/signup'}> <button type="signup">Signup</button></Link>
             </form> 
             {error && <div className="error-message">{error}</div>}
             

        </div>
    ); 
};

export default LoginForm;
