import React, { useState } from 'react';
import axios from 'axios'; // Make sure to install axios if you haven't

const RegistrationForm = () => {
    const [name, setName] = useState('');
    const [username, setUsername] = useState('');
    const [address, setAddress] = useState('');
    const [phone, setPhone] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        const userData = {
            name,
            username,
            address,
            phone,
            email,
            password,
        };

        try {
            const response = await axios.post('http://localhost:8080/api/v1/user/register', userData);
            console.log(response.data); // Handle the response accordingly, e.g., navigation or showing a success message
            alert('Registration Successful!');
        } catch (error) {
            console.error('Error registering user:', error);
            setError('Error registering user. Please try again.');
        }
    };

    return (
        <div className="container">
            <h1 style={{ color: '#095a02' }}>Registration Form</h1>
            {error && <div className="error-message">{error}</div>} {/* Display error message if needed */}
            <form id="registration-form" onSubmit={handleSubmit}>
                <label htmlFor="name-input">Full Name:</label>
                <input
                    type="text"
                    id="name-input"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                /><br />

                <label htmlFor="address-input">Address:</label>
                <input
                    type="text"
                    id="address-input"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    required
                /><br />

                <label htmlFor="phone-input">Phone No:</label>
                <input
                    type="tel"
                    id="phone-input"
                    value={phone}
                    onChange={(e) => setPhone(e.target.value)}
                    required
                /><br />

                <label htmlFor="email-input">Email:</label>
                <input
                    type="email" // Use input[type=email] for better validation
                    id="email-input"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                /><br />

                <label htmlFor='username-input'>Username</label>
                <input
                    type='text'
                    id='username-input'
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
                <br/>

                <label htmlFor='password'>Password</label>
                <input
                    type='password' // Change to password type for security
                    id='password'
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <br/>

                <button type="submit">Register</button>
            </form>
        </div>
    );
};

export default RegistrationForm;
