import React, { useState, useEffect } from 'react'; 
import Nav from '../Navigation/Nav';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const OfficerResponse = () => {
    const { eventId } = useParams();
    const [event, setEvent] = useState(null);
    const [responseDetails, setResponseDetails] = useState({
        officerName: '',
        center: '',
        responseMessage: '',
    });
    const navigate = useNavigate();

    useEffect(() => {
        axios.get(`http://localhost:8080/api/v1/event/getbyid/${eventId}`)
            .then(response => {
                setEvent(response.data);
            })
            .catch(error => {
                console.error('Error fetching event details:', error);
            });
    }, [eventId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setResponseDetails(prevDetails => ({
            ...prevDetails,
            [name]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post(`http://localhost:8080/api/v1/response/send`, {
            eventId,
            ...responseDetails
        })
        .then(() => {
            alert('Response has been sent to the user.');
            navigate('/event-response'); // Navigate to the EventResponse page
        })
        .catch(error => {
            console.error('Error sending response:', error);
        });
    };

    return (
        <>
            <Nav />
            <div className="officer-response">
                <h1>Send Response to User</h1>
                {event && (
                    <form onSubmit={handleSubmit}>
                        <p><strong>Event Name:</strong> {event.eventName}</p>
                        <label>
                            Officer Name:
                            <input
                                type="text"
                                name="officerName"
                                value={responseDetails.officerName}
                                onChange={handleChange}
                                required
                            />
                        </label>
                        <label>
                            Center:
                            <select
                                name="center"
                                value={responseDetails.center}
                                onChange={handleChange}
                                required
                            >
                                <option value="">Select Center</option>
                                <option value="Police Station">Police Station</option>
                                <option value="Fire Camp">Fire Camp</option>
                                <option value="KMKM">KMKM</option>
                            </select>
                        </label>
                        <label>
                            Response Message:
                            <textarea
                                name="responseMessage"
                                value={responseDetails.responseMessage}
                                onChange={handleChange}
                                required
                            ></textarea>
                        </label>
                        <button type="submit">Send Response</button>
                    </form>
                )}
            </div>
        </>
    );
};

export default OfficerResponse
