import { useNavigate } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const LoginForm = () => {
    const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/api/login/loginCredentials', {
        username,
        password,
      }, {
        headers: {
          'Content-Type': 'application/json',
        }
      });

      const data = response.data;
      localStorage.setItem('storedRole', data.role);
      localStorage.setItem('username', data.username);
      
      navigate('/vendor-dashboard'); // Navigate to dashboard on successful login
    } catch (error) {
      setError('Invalid username or password'); // Set error message
      console.error('login error', error);
    }
  };

  useEffect(() => {
    localStorage.removeItem('username');
    localStorage.removeItem('storedRole');
  }, []);

  const handleSignup = () => {
    navigate('/signup');
  };

