import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Nav from '../Navigation/Nav';
import { Link } from 'react-router-dom';

const EventView = () => {
  const [events, setEvents] = useState([]);
  const shehiaId = 1; // Assume the Sheha's ShehiaId is 1, replace this with dynamic fetching if needed

  useEffect(() => {
    axios.get(`http://localhost:8080/api/v1/event/byShehia/${shehiaId}`)
      .then((response) => {
        setEvents(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the events!", error);
      });
  }, [shehiaId]);

  return (
    <>
      <Nav />
      <div className="table-container">
        <h4>View Events</h4>
        {events.length > 0 ? (
          <table className="event-table">
            <thead>
              <tr>
                <th>Event ID</th>
                <th>Event Name</th>
                <th>Event Type</th>
                <th>Event Location</th>
                <th>Time Posted</th>
                <th>Image</th>
                <th>Verify</th>
              </tr>
            </thead>
            <tbody>
              {events.map((event) => (
                <tr key={event.event_id}>
                  <td>{event.event_id}</td>
                  <td>{event.event_name}</td>
                  <td>{event.event_type}</td>
                  <td>{event.event_location}</td>
                  <td>{event.time_posted}</td>
                  <td>
                    <img
                      src={`data:image/png;base64,${event.image}`}
                      alt={event.event_name}
                      className="event-image"
                    />
                  </td>
                  <td>
                    <Link to={`/verifyevent/${event.event_id}`}>
                      <button className="btn">Verify</button>
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No events to display</p>
        )}
      </div>
    </>
  );
};

export default EventView;
