import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Nav from '../Navigation/Nav';


const E = () => {

  const [events, setEvent] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:8080/api/v1/event/all")
      .then((response) => {
        setEvent(response.data)
      })
  }, []);

  const handleUpdateClick = (event) => {
    setSelectedEvent(event);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSelectedEvent({ ...selectedEvent, [name]: value });
  };

  const handleUpdateSubmit = () => {
    axios.put(`http://localhost:8080/api/v1/event/${selectedEvent.event_id}`, selectedEvent)
      .then((response) => {
        // Refresh the event list or handle the UI changes
        setSelectedEvent(null); // Close the form after update
        setEvent(events.map(ev => ev.event_id === selectedEvent.event_id ? selectedEvent : ev));
      })
  };

  return (
    <>
      <Nav />
      <div className="table-container">
        <h4>View Event</h4>
        <table className="event-table">
          <thead>
            <tr>
              <th>Event ID</th>
              <th>Event Name</th>
              <th>Event Type</th>
              <th>Event Location</th>
              <th>Time Posted</th>
              <th>Image</th>
              <th>Action</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {events.map((event) => (
              <tr key={event.event_id}>
                <td>{event.event_id}</td>
                <td>{event.event_name}</td>
                <td>{event.event_type}</td>
                <td>{event.event_location}</td>
                <td>{event.time_posted}</td>
                <td>
                  <img
                    src={`data:image/png;base64,${event.image}`}
                    alt={event.event_name}
                    className="event-image"
                  />
                </td>
                <td>
                  <button className='btn' onClick={() => handleUpdateClick(event)}>Update</button>
                </td>
                <td>
                  <button className='btn'>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        {selectedEvent && (
          <div className="update-form">
            <h4>Update Event</h4>
            <label>Event Name:</label>
            <input
              type="text"
              name="event_name"
              value={selectedEvent.event_name}
              onChange={handleInputChange}
            />
            <label>Event Type:</label>
            <input
              type="text"
              name="event_type"
              value={selectedEvent.event_type}
              onChange={handleInputChange}
            />
            <label>Event Location:</label>
            <input
              type="text"
              name="event_location"
              value={selectedEvent.event_location}
              onChange={handleInputChange}
            />
            <label>Time Posted:</label>
            <input
              type="text"
              name="time_posted"
              value={selectedEvent.time_posted}
              onChange={handleInputChange}
            />
            <button className='btn' onClick={handleUpdateSubmit}>Submit Update</button>
            <br></br>
            <br></br>
            <button className='btn' onClick={() => setSelectedEvent(null)}>Cancel</button>
          </div>
        )}
      </div>
    </>
  );
};

export default UpdateEvent;
