import { useNavigate } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import SignupModal from './SignupModal'; // Import the modal

const LoginForm = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [showModal, setShowModal] = useState(false); // State to control the modal
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.get(`http://localhost:8080/api/v1/user/login/${email}`);
            const userData = await response.data;

            if (userData.email === email && userData.password === password) {
                localStorage.setItem("role", userData.role);
                localStorage.setItem("userId", userData.userId);

                if (userData.role === "Admin") {
                    alert("Hi admin");
                }

                if (userData.role === "Person") {
                    navigate('/statistic');
                    alert("hello person");
                }

                if (userData.role === "Sheha") {
                    alert("hello sheha");
                    navigate('/statistic');
                }

                if (userData.role === "Officer") {
                    alert("Hi officer");
                    navigate('/statistic');
                }

                alert("success");
            } else {
                alert("fail");
            }

        } catch (error) {
            console.error(error);
        }
    }

    const handleSignupClick = () => {
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
    };

    const handleForgotPassword = () => {
        navigate('/forgot-password');
    };

    return (
        <div className="login-form">
            <h2 style={{ color: '#1b5374' }}>ONLINE REPORTING EVENTS SYSTEM</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="email"
                    placeholder="please enter your email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                /><br /><br />
                <input
                    type="password"
                    placeholder='enter your password'
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                /><br />
                <button type="submit">Login</button>
                <br /><br />
                <button type="button" onClick={handleSignupClick}>Signup</button>
            </form>
            {error && <div className="error-message">{error}</div>}
            
            {showModal && <SignupModal onClose={handleCloseModal} />} {/* Render modal based on state */}
        </div>
    );
};

export default LoginForm;
