import { wait } from '@testing-library/user-event/dist/utils';
import React, { useState } from 'react';


const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();
  
    const handleSubmit = async (e) => {
      e.preventDefault();
      try {
        const response = await axios.post('http://localhost:8080/api/login/loginCredentials', {
          username,
          password,
        }, {
          headers: {
            'Content-Type': 'application/json',
          }
        });
  
        const data = response.data;
        localStorage.setItem('storedRole', data.role);
        localStorage.setItem('username', data.username);
        toast.success(<CustomToast top="Success Message!" bottom="Login Succeseful..." />);
  
        setTimeout(() => {
          navigate('/app');
        }, 2000);
      } catch (error) {
        toast.error('Invalid username or password');
        console.error('login error', error);
      }
    };
  
    const location = useLocation();
  
    useEffect(() => {
      if (location.pathname === '/') {
        localStorage.removeItem('username');
        localStorage.removeItem('storedRole');
      }
    }, [location]);
  
    const handleSignup = () => {
      navigate('/signup');
    };
  
    const handleForgotPassword = () => {
      navigate('/forgot-password');
    };
  
      
      
    

    return (
        <div className="login-form">
            <h2 style={{ color: '#1b5374' }}>ONLINE REPORTING EVENTS SYSTEM</h2>
            {/* <form onSubmit={handleSubmit}> */}
                <input
                    type="text"
                    placeholder="Username"
                    
                /><br /><br />
                <input
                    type="password"
                    placeholder="Password"
                /><br />
                <button type="submit">Login</button>
            {/* </form> */}
        </div>
    );
};

export default LoginForm;
