import React, { useState, useEffect } from 'react';
import Chart from 'chart.js';

function EventStatistics() {
  const [data, setData] = useState({
    labels: ['Uhalifu', 'Moto', 'Magendo'],
    datasets: [{
      label: 'Matukio',
      data: [10, 20, 30],
      backgroundColor: [
        'rgba(255, 99, 132, 0.2)',
        'rgba(54, 162, 235, 0.2)',
        'rgba(255, 206, 86, 0.2)'
      ],
      borderColor: [
        'rgba(255, 99, 132, 1)',
        'rgba(54, 162, 235, 1)',
        'rgba(255, 206, 86, 1)'
      ],
      borderWidth: 1
    }]
  });

  const [selectedValue, setSelectedValue] = useState('all');

  useEffect(() => {
    const chartCanvas = document.getElementById('chart');
    const ctx = chartCanvas.getContext('2d');
    const chart = new Chart(ctx, {
      type: 'bar',
      data: data,
      options: {
        scales: {
          yAxes: [{
            ticks: {
              beginAtZero: true
            }
          }]
        }
      }
    });

    return () => {
      chart.destroy();
    };
  }, [data]);

  const handleSelectChange = (e) => {
    const selectedValue = e.target.value;
    setSelectedValue(selectedValue);
    const newData = getData(selectedValue);
    setData(newData);
  };

  const getData = (tukio) => {
    // hii itakupata data kutoka kwa mfumo wako kulingana na tukio
    // kwa mfano:
    if (tukio === 'uhalifu') {
      return {
        labels: ['Uhalifu', 'Moto', 'Magendo'],
        datasets: [{
          label: 'Matukio',
          data: [40, 20, 10],
          backgroundColor: [
            'rgba(255, 99, 132, 0.2)',
            'rgba(54, 162, 235, 0.2)',
            'rgba(255, 206, 86, 0.2)'
          ],
          borderColor: [
            'rgba(255, 99, 132, 1)',
            'rgba(54, 162, 235, 1)',
            'rgba(255, 206, 86, 1)'
          ],
          borderWidth: 1
        }]
      };
    } else if (tukio === 'moto') {
      return {
        labels: ['Uhalifu', 'Moto', 'Magendo'],
        datasets: [{
          label: 'Matukio',
          data: [20, 40, 30],
          backgroundColor: [
            'rgba(255, 99, 132, 0.2)',
            'rgba(54, 162, 235, 0.2)',
            'rgba(255, 206, 86, 0.2)'
          ],
          borderColor: [
            'rgba(255, 99, 132, 1)',
            'rgba(54, 162, 235, 1)',
            'rgba(255, 206, 86, 1)'
          ],
          borderWidth: 1
        }]
      };
    } else {
      return {
        labels: ['Uhalifu', 'Moto', 'Magendo'],
        datasets: [{
          label: 'Matukio',
          data: [10, 20, 30],
          backgroundColor: [
            'rgba(255, 99, 132, 0.2)',
            'rgba(54, 162, 235, 0.2)',
            'rgba(255, 206, 86, 0.2)'
          ],
          borderColor: [
            'rgba(255, 99, 132, 1)',
            'rgba(54, 162, 235, 1)',
            'rgba(255, 206, 86, 1)'
          ],
          borderWidth: 1
        }]
      };
    }
  };

  return (
    <div>
      <select value={selectedValue} onChange={handleSelectChange}>
        <option value="all">All</option>
        <option value="uhalifu">Uhalifu</option>
        <option value="moto">Moto</option>
      </select>
      <canvas id="chart" width="800" height="600"></canvas>
    </div>
  );
}

export default Ev;
