import React, { useState, useEffect } from 'react';
import Nav from '../Navigation/Nav';
import axios from 'axios';


const EventStatistics = () => {
    const [crimeevent, setCrimeEvent] = useState(0);
     useEffect(()=>{
        axios.get('http://localhost:8080/api/v1/event/count')

    .then((response)=>{
        setCrimeEvent(response.data);
    })
     })




    return (
        <> 
        <Nav />
        <div className="event-statistics">
            <h2>{crimeevent}</h2>
            <div className="card-container">
                <div className="card">
                    <h3>Crime Events</h3>
                    <p></p>
                </div>
                <div className="card">
                    <h3>Fire Events</h3>
                    <p>{crimeevent.fireEvents}</p>
                </div>
                <div className="card">
                    <h3>Smuggling Events</h3>
                    <p></p>
                </div>
            </div>
        </div>
        </>
    );
};

export default EventStatistics;
