import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Nav from '../Navigation/Nav';
import { Link } from 'react-router-dom';

const EventView = () => {
  const shehiaid = parseInt(localStorage.getItem('shehiaId'));

  const [events, setEvent] = useState([]);

  useEffect(() => {
    if (shehiaid) {
      axios.get(`http://localhost:8080/api/v1/event/byShehia/${shehiaid}`)
        .then((response) => {
          setEvent(response.data);
        })
        .catch(error => {
          console.error("There was an error fetching the events!", error);
        });
    }
  }, [shehiaid]);

  const handleDelete = (eventId) => {
    if (window.confirm('Are you sure you want to delete?')) {
      axios.delete(`http://localhost:8080/api/v1/event/delete/${eventId}`)
        .then((response) => {
          // Remove the deleted event from the state
          setEvent(events.filter(event => event.event_id !== eventId));
        })
        .catch((error) => {
          console.error("There was an error deleting the event!", error);
        });
    }
  };

  return (
    <>
      <Nav />
      <div className="table-container">
        <h4>View Event (Shehia ID: {shehiaid})</h4>
        <table className="event-table">
          <thead>
            <tr>
              <th>Event ID</th>
              <th>Event Name</th>
              <th>Event Type</th>
              <th>Event Location</th>
              <th>Time Posted</th>
              <th>Image</th>
              <th>Action</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {events.map((event) => (
              <tr key={event.event_id}>
                <td>{event.event_id}</td>
                <td>{event.event_name}</td>
                <td>{event.event_type}</td>
                <td>{event.event_location}</td>
                <td>{event.time_posted}</td>
                <td>
                  <img
                    src={`data:image/png;base64,${event.image}`}
                    alt={event.event_name}
                    className="event-image"
                  />
                </td>
                <td>
                  <Link to={`/updateevent/${event.event_id}`}>
                    <button className="btn">Update</button>
                  </Link>
                </td>
                <td>
                  <button className="btn" onClick={() => handleDelete(event.event_id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default EventView;
