import React, { useState, useEffect } from 'react';

const ViewResponse = () => {
  const [responseData, setResponseData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/v1/response/all');
        const data = await response.json();
        setResponseData(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="view-response-container">
      <h1 className="title">Response Data</h1>
      {responseData ? (
        <div className="response-data-container">
          <div className="event-data-container">
            {responseData.map((item) => (
              <div key={item.id} className="event-data">
                <h2 className="event-name">{item.event.event_name}</h2>
                <p className="event-location">Location: {item.event.event_location}</p>
                <p className="event-time-posted">Time posted: {item.event.time_posted}</p>
                <p className="event-type">Event type: {item.event.event_type}</p>
              </div>
            ))}
          </div>
          <div className="response-data">
            {responseData.map((item) => (
              <div key={item.id} className="response">
                <h2 className="response-from">Response from {item.officer.name}</h2>
                <p className="response-message">{item.responseMessage}</p>
                <p className="response-time">Response time: {item.response_Time}</p>
              </div>
            ))}
          </div>
        </div>
      ) : (
        <p className="loading-data">Loading data...</p>
      )}
    </div>
  );
};

export default ViewResponse;
